select.where:
and is_delete = '0'  
 and dept_id = #{deptId}
 and is_delete = #{isDelete}
 and is_enable = #{isEnable}
 and dept_name like concat('%', #{deptName}, '%')
 and email like concat('%', #{email}, '%')

mapper.update:
update bs_contact
dept_id = #{deptId}, is_delete = #{isDelete}, is_enable = #{isEnable}, update_time = #{updateTime}, update_by = #{updateBy}, dept_name = #{deptName}, email = #{email}, remark = #{remark},
where id = #{id}
