mapper.select:
select ui.bs_user_labtest_item_id, ui.user_id, ui.bs_labtest_items_id, ui.remark, ui.is_deletedelete_id, ui.create_time, ui.user_type, ui.create_by, ui.update_time, ui.update_by,u.nick_name,u.user_name,i.item_name,i.item_code from bs_user_labtest_item ui inner join sys_user u on u.user_id=ui.user_id inner join bs_labtest_items i on i.bs_labtest_items_id=ui.bs_labtest_items_id
and ui.delete_id = '0' and i.is_delete='0' and i.is_enable='1' and u.del_flag='0' and u.status='0' and ui.bs_labtest_items_id=#{labtestItemsId} and u.dept_id=#{deptId}
order by ui.create_time desc

SELECT user_id FROM (select distinct ui.user_id,ui.create_time from bs_user_labtest_item ui inner join sys_user u on u.user_id=ui.user_id inner join bs_labtest_items i on i.bs_labtest_items_id=ui.bs_labtest_items_id
and ui.delete_id = '0' and i.is_delete='0' and i.is_enable='1' and u.del_flag='0' and u.status='0' and ui.bs_labtest_items_id=#{labtestItemsId} and u.dept_id=#{deptId}
) t ORDER BY create_time DESC

select ui.bs_user_labtest_item_id, ui.user_id, ui.bs_labtest_items_id, ui.remark, ui.delete_id, ui.create_time, ui.user_type, ui.create_by, ui.update_time, ui.update_by,u.nick_name,u.user_name,i.item_name,i.item_code from bs_user_labtest_item ui inner join sys_user u on u.user_id=ui.user_id inner join bs_labtest_items i on i.bs_labtest_items_id=ui.bs_labtest_items_id where ui.user_id in
#{userId}
and ui.delete_id = '0' and i.is_delete='0' and i.is_enable='1' and u.del_flag='0' and u.status='0' and ui.bs_labtest_items_id=#{userLabtestItem.labtestItemsId} and u.dept_id=#{userLabtestItem.deptId} order by ui.create_time desc

mapper.update:
update bs_user_labtest_item
user_id = #{userId}, bs_labtest_items_id = #{labtestItemsId}, remark = #{remark}, update_time = #{updateTime}, update_by = #{updateBy}, user_type = #{userType},
where bs_user_labtest_item_id = #{userLabtestItemId}
