select.where:
and delete_id = '0'  
 and base_id = #{baseId}
 and sample_no like concat('%', #{sampleNo}, '%')
 and pcr_entrust_order_sample_id = #{pcrEntrustOrderSampleId}
 and item_id = #{itemId}
 and item_name like concat('%', #{itemName}, '%')
 and test_result = #{testResult}
 and remark like concat('%', #{remark}, '%')

mapper.update:
update op_jczx_pcr_result_info
base_id = #{baseId}, dept_name = #{deptName}, sample_no = #{sampleNo}, sample_name = #{sampleName}, pcr_entrust_order_sample_id = #{pcrEntrustOrderSampleId}, item_id = #{itemId}, pcr_entrust_order_item_id = #{pcrEntrustOrderItemId}, item_name = #{itemName}, test_result = #{testResult}, sequence = #{sequence}, remark = #{remark}, update_time = #{updateTime}, update_by = #{updateBy}, tqsjh = #{tqsjh}, kzsjh = #{kzsjh}, file_id = #{fileId}, invbill_name = #{invbillName},
where op_jczx_pcr_result_info_id = #{opJczxPcrResultInfoId}

UPDATE op_jczx_pcr_result_info
SET remark = #{remark}
WHERE sample_no = #{sampleNo}
  AND pcr_entrust_order_item_id = #{pcrEntrustOrderItemId}
