select.where:
and is_delete = '0'  
 and dept_id = #{deptId}
 and is_delete = #{isDelete}
 and is_enable = #{isEnable}
 and dept_name like concat('%', #{deptName}, '%')
 and entrust_order_no = #{entrustOrderNo}
 and entrust_time >= #{sendSampleDateStart}
 and entrust_time <= #{sendSampleDateEnd}
 and sample_quantity = #{sampleQuantity}
 and entrust_lab = #{entrustLab}
 and file_addres = #{fileAddres}

mapper.select:
select outentrust_register_id, outentrust_register_sample_id, is_delete, create_time, create_by, update_time, update_by, sample_no, sample_name, item_name,item_id
from op_outentrust_register_sample
where outentrust_register_sample_id = #{outentrust_register_sample_id}

mapper.update:
update op_outentrust_register
dept_id = #{deptId}, is_enable = #{isEnable}, update_time = #{updateTime}, update_by = #{updateBy}, dept_name = #{deptName}, remark = #{remark}, entrust_order_no = #{entrustOrderNo}, entrust_time = #{entrustTime}, sample_quantity = #{sampleQuantity}, entrust_lab = #{entrustLab}, file_addres = #{fileAddres},
where id = #{id}

update op_outentrust_register_sample
set is_delete=1,
update_time=#{now,jdbcType=TIMESTAMP},
update_by=#{updateUserId}
where outentrust_register_sample_id in

    #{item}
