mapper.select:
select d.dept_name, '' as pcr_task_item_type, fo.status, '1' as type, fo.entrust_order_no, COUNT(DISTINCT fos.op_feed_entrust_order_sample_id) AS sample_amount, /* 修正了原SQL中 test_method 缺少 SEPARATOR 关键字的语法瑕疵 */ GROUP_CONCAT(DISTINCT fos.test_method SEPARATOR ',') as test_method,
/* --- 修改开始 --- */ /* 使用 CONCAT_WS 自动处理逗号。如果存在 item_name，则用逗号连接；如果不存在，则直接显示 Dairyland */ CONCAT_WS(',', GROUP_CONCAT(DISTINCT i.item_name SEPARATOR ','), CASE /* 判断该订单下是否有任意一个样品的 test_method 为 1 */ WHEN SUM(IF(fos.test_method = '1', 1, 0)) > 0 THEN 'Dairyland近红外' ELSE NULL END ) AS item_name, /* --- 修改结束 --- */
fo.remark, fo.requires_judgement, fo.allows_subcontracting, fo.report_receive_type, fo.send_sample_date, fo.receive_time, fo.op_feed_entrust_order_id as op_entrust_order_id, fo.create_time, fo.is_return, fo.return_reason from op_feed_entrust_order fo left join op_feed_entrust_order_sample fos on fos.feed_entrust_order_id=fo.op_feed_entrust_order_id and fos.is_delete='0' left join op_feed_entrust_order_item fi on fi.op_feed_entrust_order_sample_id =fos.op_feed_entrust_order_sample_id and fi.is_delete='0' left join bs_labtest_items i on i.bs_labtest_items_id=fi.item_id and i.is_delete='0' left join sys_dept d on fo.entrust_dept_id=d.dept_id and d.del_flag='0'
and fo.delete_id = '0' and fo.is_return = '0' AND (fo.receiver_id IS NULL OR fo.receiver_id = '') and fo.status!='0' and fo.entrust_order_no like concat('%', #{entrustOrderNo}, '%') and d.dept_name like concat('%', #{deptName}, '%') and fo.send_sample_date >= #{sendSampleDateStart} and fo.send_sample_date <= #{sendSampleDateEnd}
GROUP BY fo.op_feed_entrust_order_id order by fo.send_sample_date desc,fo.create_time desc

select d.dept_name, '' as pcr_task_item_type, fo.status, fo.entrust_order_no, fos.sample_no, fos.name as sample_name, '1' as type, MAX(fos.test_method) as test_method,
/* --- 修改开始 --- */ /* 使用 CONCAT_WS，如果 test_method 是 '1'，追加 Dairyland近红外 */ CONCAT_WS(',', GROUP_CONCAT(DISTINCT i.item_name SEPARATOR ','), CASE /* 因为是按样品分组，直接判断当前样品的 test_method 即可 */ WHEN MAX(fos.test_method) = '1' THEN 'Dairyland近红外' ELSE NULL END ) AS item_name, /* --- 修改结束 --- */
fo.remark, fo.requires_judgement, fo.allows_subcontracting, fo.report_receive_type, fo.send_sample_date, fo.receive_time, fo.op_feed_entrust_order_id as op_entrust_order_id, fo.create_time, fo.is_return, fo.return_reason from op_feed_entrust_order fo left join op_feed_entrust_order_sample fos on fos.feed_entrust_order_id=fo.op_feed_entrust_order_id and fos.is_delete='0' left join op_feed_entrust_order_item fi on fi.op_feed_entrust_order_sample_id =fos.op_feed_entrust_order_sample_id and fi.is_delete='0' left join bs_labtest_items i on i.bs_labtest_items_id=fi.item_id and i.is_delete='0' left join sys_dept d on fo.entrust_dept_id=d.dept_id and d.del_flag='0'
and fo.delete_id = '0' and fo.is_return = '0' AND fo.receiver_id IS NOT NULL AND fo.receiver_id != '' and fo.entrust_order_no like concat('%', #{entrustOrderNo}, '%') and d.dept_name like concat('%', #{deptName}, '%') and fo.send_sample_date >= #{sendSampleDateStart} and fo.send_sample_date <= #{sendSampleDateEnd}
GROUP BY fos.op_feed_entrust_order_sample_id, fo.op_feed_entrust_order_id, d.dept_id
order by fo.send_sample_date desc,fo.create_time desc

select d.dept_name, '' as pcr_task_item_type, fo.status, fo.entrust_order_no, fos.sample_no, fos.name as sample_name, '1' as type, MAX(fos.test_method) as test_method, fo.remark, fo.requires_judgement, fo.allows_subcontracting, fo.report_receive_type, fo.send_sample_date, fo.receive_time, fo.op_feed_entrust_order_id as op_entrust_order_id, fo.create_time, fo.is_return, fos.jhw_no, fo.return_reason, fos.batch_no from op_feed_entrust_order fo left join op_feed_entrust_order_sample fos on fos.feed_entrust_order_id=fo.op_feed_entrust_order_id and fos.is_delete='0' left join op_feed_entrust_order_item fi on fi.op_feed_entrust_order_sample_id =fos.op_feed_entrust_order_sample_id and fi.is_delete='0' left join bs_labtest_items i on i.bs_labtest_items_id=fi.item_id and i.is_delete='0' left join sys_dept d on fo.entrust_dept_id=d.dept_id and d.del_flag='0'
and fo.delete_id = '0' and fo.is_return = '0' AND fo.receiver_id IS NOT NULL AND fo.receiver_id != '' and fos.test_method='1' and fo.entrust_order_no like concat('%', #{entrustOrderNo}, '%') and d.dept_name like concat('%', #{deptName}, '%') and fo.send_sample_date >= #{sendSampleDateStart} and fo.send_sample_date <= #{sendSampleDateEnd}
order by fo.send_sample_date desc,fo.create_time desc
