mapper.select:

left join sys_dept d on a.dept_id = d.dept_id

    a.is_delete = '0' and a.status = '0'
     and a.sampling_order_number like concat('%',#{samplingOrderNumber},'%')
     and a.plan_time = #{planTime}
     and a.status = #{status}
     and a.is_delete = #{isDelete}
     and a.dept_id = #{deptId}

        AND a.plan_type IN

            #{type}

mapper.update:
update op_finished_product_sampling_plan
sampling_order_number = #{samplingOrderNumber}, plan_time = #{planTime}, plan_type = #{planType}, status = #{status}, remark = #{remark}, update_by = #{updateBy}, update_time = #{updateTime}, is_delete = #{isDelete}, dept_id = #{deptId},
where finished_product_sampling_plan_id = #{finishedProductSamplingPlanId}

update op_finished_product_sampling_plan_detail
material_code = #{materialCode}, material_name = #{materialName}, planned_production_volume = #{plannedProductionVolume}, planned_sample_quantity = #{plannedSampleQuantity}, have_sample_copies = #{haveSampleCopies},
where finished_product_sampling_plan_detail_id = #{finishedProductSamplingPlanDetailId}
